/*
Credit: 
https://github.com/lottie-react-native/lottie-react-native/issues/1005
https://colorize-react-native-lottie.netlify.app/
*/

import fs from "fs";
import { set } from "lodash";
import tinycolor from "tinycolor2";
import LOTTIE_SOURCE from "./lottie.json";

// values of colorByPath can be falsy to no-op
function colorizeLottie(json, colorByPath) {
  const nextJson = JSON.parse(JSON.stringify(json));

  Object.entries(colorByPath).forEach(([path, color]) => {
    // incase undefined/null/falsy is passed to color
    if (!color) return;
    const rgbPercentages = tinycolor(color).toPercentageRgb();
    const rFraction = parseInt(rgbPercentages.r, 10) / 100;
    const gFraction = parseInt(rgbPercentages.g, 10) / 100;
    const bFraction = parseInt(rgbPercentages.b, 10) / 100;

    const pathParts = path.split(".");
    set(nextJson, [...pathParts, 0], rFraction);
    set(nextJson, [...pathParts, 1], gFraction);
    set(nextJson, [...pathParts, 2], bFraction);
  });

  return nextJson;
}

function writeToFile(data, fileName) {
  fs.writeFileSync(fileName, JSON.stringify(data, null, 2));
  console.log(`Output written to ${fileName}`);
}

function Example() {
  const colorizedSource = colorizeLottie(LOTTIE_SOURCE, {
    // small 10.Ellipse 1.Fill 1
    "layers.0.shapes.0.it.1.c.k": "#ffffff",
    // small 9.Ellipse 1.Fill 1
    "layers.1.shapes.0.it.1.c.k": "#ffffff",
    // small 8.Ellipse 1.Fill 1
    "layers.2.shapes.0.it.1.c.k": "#ffffff",
    // small 11.Ellipse 1.Fill 1
    "layers.3.shapes.0.it.1.c.k": "#ffffff",
    // small 7.Ellipse 1.Fill 1
    "layers.4.shapes.0.it.1.c.k": "#ffffff",
    // small 6.Ellipse 1.Fill 1
    "layers.5.shapes.0.it.1.c.k": "#ffffff",
    // small 5.Ellipse 1.Fill 1
    "layers.6.shapes.0.it.1.c.k": "#ffffff",
    // small 4.Ellipse 1.Fill 1
    "layers.7.shapes.0.it.1.c.k": "#ffffff",
    // small 12.Ellipse 1.Fill 1
    "layers.8.shapes.0.it.1.c.k": "#ffffff",
    // small 3.Ellipse 1.Fill 1
    "layers.9.shapes.0.it.1.c.k": "#ffffff",
    // small 2.Ellipse 1.Fill 1
    "layers.10.shapes.0.it.1.c.k": "#ffffff",
    // small 13.Ellipse 1.Fill 1
    "layers.11.shapes.0.it.1.c.k": "#ffffff",
    // small.Ellipse 1.Fill 1
    "layers.12.shapes.0.it.1.c.k": "#ffffff",
    // medium 3.Ellipse 1.Fill 1
    "layers.13.shapes.0.it.1.c.k": "#ffffff",
    // medium 4.Ellipse 1.Fill 1
    "layers.14.shapes.0.it.1.c.k": "#ffffff",
    // medium 5.Ellipse 1.Fill 1
    "layers.15.shapes.0.it.1.c.k": "#ffffff",
    // medium 2.Ellipse 1.Fill 1
    "layers.16.shapes.0.it.1.c.k": "#ffffff",
    // medium.Ellipse 1.Fill 1
    "layers.17.shapes.0.it.1.c.k": "#ffffff",
    // large 3.Ellipse 1.Fill 1
    "layers.18.shapes.0.it.1.c.k": "#ffffff",
    // large 6.Ellipse 1.Fill 1
    "layers.19.shapes.0.it.1.c.k": "#ffffff",
    // large 2.Ellipse 1.Fill 1
    "layers.20.shapes.0.it.1.c.k": "#ffffff",
    // large 5.Ellipse 1.Fill 1
    "layers.21.shapes.0.it.1.c.k": "#ffffff",
    // large.Ellipse 1.Fill 1
    "layers.22.shapes.0.it.1.c.k": "#ffffff",
  });

  return colorizedSource;
}

function App() {
  writeToFile(Example(), "output.json");
}

App();
